const data: Record<FormFields, number | string> = {
    name: "name",
    surname: "surname",
    age: 21,
}

const newAge = data.age * 2;
const nameUpperCase = data.name.toUpperCase();

class Person {
  age: number = 0

  changeAge() {
    console.log("Logger: Func start")
    console.log("changing age...")
    console.log("Logger: Func end")
  }
}

const person = new Person();
person.changeAge()

function Logger<This, Args extends number[], Return>(
    target: (this: This, ...args: Args) => Return, 
    context: ClassMethodDecoratorContext<This, (this: This, ...args: Args) => Return>
) {
  return function(this: This, ...args: Args) {
    console.log("Logger: Func start")
    const result = target.call(this, ...args)
    console.log("Logger: Func end")

    return result
  }
}

import useEventListener from "../useEventListener/useEventListener"

export default function useClickOutside(ref, cb) {
    useEventListener("click", e => {
        if (ref.current == null || ref.current.contains(e.target)) return
        cb(e)
    }, document)
}

import { useRef, useState } from "react"
import useClickOutside from "./useClickOutside"

export default function ClickOutsideComponent() {
    const [open, setOpen] = useState(false)
    const modalRef = useRef()

    useClickOutside(modalRef, () => {
        if (open) setOpen(false)
    })

    return (
        <>
            <button onClick={() => setOpen(true)}>Open</button>
            <div
                ref={modalRef}
                style={{
                    display: open ? "block" : "none",
                    backgroundColor: "blue",
                    color: "white",
                    width: "100px",
                    height: "100px",
                    position: "absolute",
                    top: "calc(50% - 50px)",
                    left: "calc(50% - 50px)",
                }}
            >
                <span>Modal</span>
            </div>
        </>
    )
}

